    private void UpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_UpdateActionPerformed
        DefaultTableModel model = (DefaultTableModel) subjectDegree.getModel();
        String degree = deg1.getText();
        String subject = subj1.getText();
        model.setValueAt(degree, subjectDegree.getSelectedRow(), 1);
        try {
            String url = "jdbc:ucanaccess://Data Base/ComputerECTS.accdb";
            Connection conn = DriverManager.getConnection(url);
            Statement st = conn.createStatement();

            String sql = "Update SubjectAndDegree Set Degree = '" + degree + "' " + " WHERE SubjectName = '" + subject + "'";
            st.executeUpdate(sql);
            st.close();
            conn.close();
        } catch (Exception e) {
            System.out.println(e.toString());
        }
    }//GEN-LAST:event_UpdateActionPerformed

