    private void searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchActionPerformed
        try {
            String url = "jdbc:ucanaccess://Data Base/ComputerECTS.accdb";
            Connection conn = DriverManager.getConnection(url);
            String sql = "SELECT StudentID FROM Students WHERE FullName = ?";
            PreparedStatement pst = conn.prepareStatement(sql);
            pst.setString(1, studentName.getText().trim());
            ResultSet rs = pst.executeQuery();
            if (rs.next()) {
                String studentID = rs.getString("StudentID");
                String sql1 = "SELECT SubjectName, Degree FROM SubjectAndDegree WHERE StudentID = ?";
                PreparedStatement pst1 = conn.prepareStatement(sql1);
                pst1.setString(1, studentID);
                ResultSet rs1 = pst1.executeQuery();
                DefaultTableModel model = (DefaultTableModel) subjectDegree.getModel();
                model.setRowCount(0);
                while (rs1.next()) {
                    String subjectName = rs1.getString("SubjectName");
                    String degree = rs1.getString("Degree");
                    model.addRow(new Object[]{subjectName, degree});
                }
                rs1.close(); pst1.close();
            }
            rs.close(); pst.close(); conn.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_searchActionPerformed

